import numpy as np import matplotlib.pyplot as pltimport pandas as pdfrom sklearn.model_selection import train_test_splitfrom sklearn.preprocessing import LabelEncoder, OneHotEncoderdataset = pd.read_csv('50-startups-data.csv');X = dataset.iloc[:,:-1].valuesy = dataset.iloc[:,4].valueslabelencoder_X = LabelEncoder()X[:,3] = labelencoder_X.fit_transform(X[:,3])onehotencoder = OneHotEncoder(categorical_features = [3])X = onehotencoder.fit_transform(X).toarray()X = X[:, 1:]X_train, X_test, y_train, y_test = train_test_split(X, y, test_size = 0.2, random_state = 0)